import React, { useState } from "react";
import styled from "styled-components";
import AddToCartButton from "./AddToCartButton";
import PriceDisplay from "./PriceDisplay";
import QuantitySelect from "./QuantitySelect";

const CartManager = (props) => {
  let [qty, setQty] = useState(1);

  //set number of items to be added to the cart
  const selectQuantity = (e) => {
    if (e.target.value === "+") {
      setQty(qty + 1);
    } else if ((e.target.value === "-") & (qty > 1)) {
      setQty(qty - 1);
    }
  };

  const Wrapper = styled.div`
    background-color: #05102a;
    display: flex;
    padding: 15px 30px;
    flex-direction: column;
    justify-content: center;
    align-items: center; ;
  `;
  const PriceWrapper = styled.div`
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: flex-end;
  `;

  return (
    <Wrapper>
      <PriceWrapper>
        <PriceDisplay price={props.price} />
        <QuantitySelect changeQuantity={props.selectQuantity} qty={qty} />
      </PriceWrapper>
      <AddToCartButton />
    </Wrapper>
  );
};

export default CartManager;
