import React from "react";
import styled from "styled-components";
import Logo from "../assets/logo.svg";
import Basket from "../assets/basket.svg";
import ProductSpec from "./ProductSpec";

const Wrapper = styled.div`
  display: flex;
  width: 100vw;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  background-color: #05102a;
`;

const LogoStyle = styled.img`
  height: 20px;
  margin: 7px;
`;

const BasketWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const BasketCount = styled.div`
  height: 20px;
  width: 20px;
  background-color: #fa1c7b;
  border-radius: 50%;
  color: white;
  text-align: center;
  font-weight: bold;
`;

const Nav = () => {
  const getNumberofItems = () => {
    let totalItems;
    for (var i = 0; i < props.cartItems.length; i++) {
      totalItems = totalItems + props.cartItems[i].quantity;
    }
    return totalItems;
  };

  return (
    <Wrapper>
      <LogoStyle src={Logo} />
      <BasketWrapper>
        <BasketCount>{() => getNumberofItems}</BasketCount>
        <LogoStyle src={Basket} />
      </BasketWrapper>
    </Wrapper>
  );
};

export default Nav;
