import React from "react";
import styled from "styled-components";
import data from "../data";
import CenteredImage from "./CenteredImage";
import ProductTitle from "./ProductTitle";
import ProductSpec from "./ProductSpec";
import CartManager from "./CartManager";
import ItemDescription from "./ItemDescription";

const OneProduct = () => {
  const ProductOverview = styled.div`
    background: linear-gradient(#05102a 60%, #02193b 60%);
    width: 100vw;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
  `;

  const ProductInfo = styled.div`
    padding: 0 30px 5px 30px;
  `;

  const OneSection = styled.div`
    width: 100%;
    background: ${(props) => props.backgroundColor};
    color: white;
  `;
  return (
    <>
      <ProductOverview>
        <CenteredImage url={data.product.imgUrl} />
        <ProductInfo>
          <ProductTitle data={data.product.name} />
          <ProductSpec
            power={data.product.power}
            quantity={data.product.quantity}
          />
        </ProductInfo>
      </ProductOverview>

      <OneSection backgroundColor={"#05102A"}>
        <CartManager price={data.product.price} />
      </OneSection>

      <OneSection>
        <ItemDescription />
      </OneSection>
    </>
  );
};

export default OneProduct;
