import React from "react";
import styled from "styled-components";
import data from "../data";
import ProductTitle from "../components/ProductTitle";

const OneProduct = () => {
  const ProductOverview = styled.div`
    background: linear-gradient(#05102a 70%, #02193b 70%);
    width: 100vw;
    height: 400px;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start; ;
  `;

  const ImageWrapper = styled.div`
    position: relative;
    width: 100%;
    height: 75%;
    display: flex;
    flex-direction: column;
    align-items: center;
  `;

  const ProductImage = styled.img`
    max-width: 100%;
    max-height: 100%;
    position: absolute;
  `;

  return (
    <>
      <ProductOverview>
        <ImageWrapper>
          <ProductImage src={data.product.imgUrl} />
        </ImageWrapper>
      </ProductOverview>
    </>
  );
};

export default OneProduct;
