import React from "react";
import styled from "styled-components";
import data from "../data";
import CenteredImage from "./CenteredImage";
import ProductTitle from "./ProductTitle";
import ProductSpec from "./ProductSpec";

const OneProduct = () => {
  let data = data.product;

  const ProductOverview = styled.div`
    background: linear-gradient(#05102a 70%, #02193b 70%);
    width: 100vw;
    height: 400px;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
  `;

  const OneSection = styled.div`
    margin: 0 20px;
    /* padding: 5px 5vw; */
    background: ${(props) => props.backgroundColor};
    color: white;
  `;
  return (
    <>
      <ProductOverview>
        <CenteredImage url={data.imgUrl} />
        <OneSection>
          <ProductTitle data={data.name} />
          <ProductSpec power={data.power} quantity={data.quantity} />
        </OneSection>
      </ProductOverview>

      <OneSection backgroundColor={"#05102A"}></OneSection>
    </>
  );
};

export default OneProduct;
